// Happy path.
func TestNetworkDefinition_DeepCopy(t *testing.T) {
	netDef, err := Unmarshal([]byte(ExampleNDF))
	if err != nil {
		t.Fatalf("Failed to create new NetworkDefinition: %+v", err)
	}

	newNDF := netDef.DeepCopy()

	if !reflect.DeepEqual(netDef, newNDF) {
		t.Errorf("DeepCopy() failed to return copy of NDF."+
			"\nexpected: %+v\nreceived: %+v", netDef, netDef)
	}

	netDef.Gateways = append(netDef.Gateways, Gateway{[]byte("ID"), "address", "TlsCertificate", 0})

	if reflect.DeepEqual(netDef, newNDF) {
		t.Errorf("DeepCopy() failed to make a deep copy. Changes in original "+
			"reflected in copy.\nexpected: %+v\nreceived: %+v", netDef, netDef)
	}

	if reflect.DeepEqual(netDef.Gateways, newNDF.Gateways) {
		t.Errorf("DeepCopy() failed to make a deep copy of Gateways."+
			"\nexpected: %+v\nreceived: %+v",
			netDef.Gateways, netDef.Gateways)
	}

	netDef.Nodes[0].ID = []byte("new ID")

	if reflect.DeepEqual(netDef.Nodes, newNDF.Nodes) {
		t.Errorf("DeepCopy() failed to make a deep copy of Nodes."+
			"\nexpected: %+v\nreceived: %+v",
			netDef.Nodes, netDef.Nodes)
	}

	netDef.UDB.ID = []byte("new ID")

	if reflect.DeepEqual(netDef.UDB, newNDF.UDB) {
		t.Errorf("DeepCopy() failed to make a deep copy of UDB."+
			"\nexpected: %+v\nreceived: %+v",
			netDef.UDB, netDef.UDB)
	}

	netDef.Timestamp = time.Now()

	if reflect.DeepEqual(netDef.Timestamp, newNDF.Timestamp) {
		t.Errorf("DeepCopy() failed to make a deep copy of Timestamp."+
			"\nexpected: %+v\nreceived: %+v",
			netDef.Timestamp, netDef.Timestamp)
	}
}
